mapper.select:
select sys_login_log.id as id, sys_login_log.admin_id as adminId, sa.username as adminName, sys_login_log.admin_ip as adminIP, sys_login_log.admin_os as adminOS, sys_login_log.login_status as loginStatus, sys_login_log.login_time as loginTime from sys_login_log left join sys_admin sa on sys_login_log.admin_id = sa.id
and sys_login_log.id = #{id}
and sa.username like concat('%',#{username},'%')
and status = #{status}
and sys_login_log.login_time = #{login_time}
